import { Figure } from "../../../data/types";
import classname from "./GraphElement.module.css";
export function ShowGraphElement(Element: Figure, zoom: number) {
  return (
    <>
      <svg
        className={classname.pos}
        key={Element.id}
        style={{
          width: Element.width * zoom + "%",
          height: Element.height * zoom + "%",
          left: Element.pos.left + "%",
          top: Element.pos.top + "%",
        }}
      >
        {Element.shape === "triangle" && (
          <g>
            <g>
              <polygon
                points="256,30 486,472 26,472"
                fill={Element.innerColor}
                stroke={Element.borderColor}
                strokeWidth={25}
              />
            </g>
          </g>
        )}
        {Element.shape === "square" && (
          <g>
            <g>
              <polygon
                points="25,25 475,25 475,475 25,475"
                fill={Element.innerColor}
                stroke={Element.borderColor}
                strokeWidth={25}
              />
            </g>
          </g>
        )}
        {Element.shape === "circle" && (
          <g>
            <g>
              <circle
                cx={256}
                cy={256}
                r={250 - 25 / 2}
                fill={Element.innerColor}
                stroke={Element.borderColor}
                strokeWidth={25}
              />
            </g>
          </g>
        )}
      </svg>
      <div
        style={{
          cursor: "nwse-resize",
          position: "absolute",
          width: "5px",
          height: "5px",
          left: Element.pos.left + Element.width - 3.5,
          top: Element.pos.top + Element.height - 3.5,
        }}
      />
    </>
  );
}
