import React from "react";
import {
    Presentation,
    SlideElement,
    Figure,
    Picture,
    TextBox,
    GeneralElementType,

} from "../data/types";



function ShowTextElement(Element: TextBox) {
    return (
        <p style={
            {
                font: Element.font
            }
        }>
            {Element.content}
        </p>
    );
}

function ShowImageElement(Element: Picture) {
    return (
        <img className='pos' src={require("../img/kot.jpg")} alt="jopa" style={
            {
                width: Element.size,
                height: Element.size,
                top: Element.posY,
                left: Element.posX,
            }
        } />
    );
}

function ShowTriangle(Element: Figure) {
    return (
        <img className='pos' src={require(`../img/shape-${Element.shape}.svg`)} alt="kek" style={
            {
                width: Element.size,
                height: Element.size,
                top: Element.posY,
                left: Element.posX,
            }
        } />
    );
}


function SelectTypeOfElement(Element: GeneralElementType) {
    switch (Element.type) {
        case "Text":
            return ShowTextElement(Element)
        case "Image":
            return ShowImageElement(Element);
        case "Figure":
            return ShowTriangle(Element);
    }
}


export function ShowSlider(presentation: Presentation) {
    return (
        <ul className="list">
            {presentation.slide.map((slide) => (
                <li>
                    {slide.elements.map((element) => (
                        SelectTypeOfElement(element)
                    ))}
                </li>
            )

            )}
        </ul>
    );
}

export function ShowSlide(presentation: Presentation) {
    return (
        <ul className="list">
            <li>
                {/* {ShowElement(slide.elements[0])} */}
                {presentation.slide[0].elements.map((element) => (
                    SelectTypeOfElement(element)
                ))}
            </li>
        </ul>
    );
}
