import React from "react";
import {
    Presentation,
    Figure,
    Picture,
    TextBox,
    GeneralElementType,

} from "../data/types";



function ShowTextElement(Element: TextBox) {
    return (
        <p style={
            {
                font: Element.font
            }
        }>
            {Element.content}
        </p>
    );
}

function ShowImageElement(Element: Picture) {
    return (
        <img className='pos' src={require("../img/kot.jpg")} alt="jopa" style={
            {
                width: Element.size.width + "%",
                height: Element.size.height + "%",
                top: Element.pos.top + "%",
                left: Element.pos.left + "%",
            }
        } />
    );
}

function ShowGraphElement(Element: Figure) {
    return (
        <img className='pos' src={require(`../img/shape-${Element.shape}.svg`)} alt="kek"
            style={
                {
                    width: Element.size.width + "%",
                    height: Element.size.height + "%",
                    top: Element.pos.top + "%",
                    left: Element.pos.left + "%",
                }
            } />
    );
}


function SelectTypeOfElement(Element: GeneralElementType) {
    switch (Element.type) {
        case "Text":
            return ShowTextElement(Element)
        case "Image":
            return ShowImageElement(Element);
        case "Figure":
            return ShowGraphElement(Element);
    }
}

export function ShowSlider(presentation: Presentation) {
    return (
        <ul className="list">
            {presentation.slide.map((slide) => (
                <li className="list__child">
                    {slide.elements.map((element) => (
                        SelectTypeOfElement(element)
                    ))}
                </li>
            )

            )}
        </ul>
    );
}

export function ShowSlide(presentation: Presentation) {
    return (
        <div>
            {presentation.slide[0].elements.map((element) => (
                <div>
                    {SelectTypeOfElement(element)}
                </div>
            ))}
        </div>

    );
}
