import React from "react";
import {
    Presentation,
    SlideElement,
    Figure,
    Picture,
    TextBox,
    GeneralElementType,

} from "../data/types";



function ShowTextElement(Element: TextBox) {
    return (
        <p>
            {Element.content}
        </p>
    );
}

function ShowImageElement(Element: Picture) {
    return (
        <img src={require("../img/kot.jpg")} alt="jopa" style={{ width: Element.size, height: Element.size }} />
    );
}

function ShowTriangle(Element: string) {
    return (
        <img src={require(Element)} alt="kek" />
    );
}

function ShowSquare(Element: string) {
    return (
        <img src={require(Element)} alt="kek" />
    );
}

function ShowCircle(Element: string) {
    return (
        <img src={require(Element)} alt="kek" />
    );
}

function SelectTypeOfElement(Element: GeneralElementType) {
    switch (Element.type) {
        case "Text":
            return ShowTextElement(Element)
        case "Image":
            return ShowImageElement(Element);
        case "Figure":
            if (Element.shape === "triangle") {
                return ShowTriangle(Element.shape);
            }
            else if (Element.shape === "square") {
                return ShowSquare(Element.shape);
            }
            else {
                return ShowCircle(Element.shape);
            }
    }
}


export function ShowSlide(presentation: Presentation) {
    return (
        <ul>
            {presentation.slide.map((slide) => (
                <li>
                    {/* {ShowElement(slide.elements[0])} */}
                    {slide.elements.map((element) => (
                        SelectTypeOfElement(element)
                    ))}
                </li>
            )

            )}
        </ul>
    );
}
